import React, { useEffect, useState } from "react";
import { collection, getDocs } from "firebase/firestore";
import db from "../firebase";
import { useDispatch } from "react-redux";
import { addCart } from "../redux/action";
import "./Products.css";

import { Link } from "react-router-dom";
import toast from "react-hot-toast";
import "animate.css";

const Products = () => {
  const [products, setProducts] = useState([]);
  const [filter, setFilter] = useState([]);
  const [loading, setLoading] = useState(true);
  const dispatch = useDispatch();

  const addProduct = (product) => {
    dispatch(addCart(product));
    toast.success("Ajouté au panier !");
  };

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const querySnapshot = await getDocs(collection(db, "products"));
        const productList = querySnapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data(),
        }));
        setProducts(productList);
        setFilter(productList);
        setLoading(false);
      } catch (error) {
        console.error("Erreur lors de la récupération des produits:", error);
      }
    };
    fetchProducts();
  }, []);

  const filterProducts = (category) => {
    if (category === "Tout") {
      setFilter(products);
    } else {
      setFilter(products.filter((product) => product.category === category));
    }
  };

  return (
    <div className="container my-5">
      <h2 className="display-5 text-center mb-4">Nos Produits</h2>
      <hr />

      {/* Boutons de filtrage */}
      <div className="text-center mb-4">
        <button className="btn btn-outline-dark mx-2" onClick={() => filterProducts("Tout")}>
          Tout
        </button>
        <button className="btn btn-outline-dark mx-2" onClick={() => filterProducts("Vêtements")}>
          Vêtements
        </button>
        <button className="btn btn-outline-dark mx-2" onClick={() => filterProducts("Téléphonie")}>
          Téléphonie
        </button>
        <button className="btn btn-outline-dark mx-2" onClick={() => filterProducts("Électronique")}>
          Électronique
        </button>
      </div>

      {loading ? (
        <div className="text-center my-5">
          <div className="spinner-border text-danger" role="status">
            <span className="visually-hidden">Chargement...</span>
          </div>
        </div>
      ) : (
        <div className="row">
          {filter.map((product) => (
            <div key={product.id} className="col-md-4 mb-4 animate__animated animate__fadeInUp">
              <div className="card shadow-sm h-100 text-center">
                <img
                  src={product.image}
                  alt={product.name}
                  className="card-img-top p-3"
                  style={{ height: "300px", objectFit: "cover" }}
                />
                <div className="card-body">
                  <h5 className="card-title">{product.name}</h5>
                  <p className="card-text text-muted">{product.description}</p>
                </div>
                <ul className="list-group list-group-flush">
                  <li className="list-group-item lead">Prix : {product.price} €</li>
                </ul>
                <div className="card-footer bg-white">

                  <button
                    className="btn btn-outline-danger m-1"
                    onClick={() => addProduct(product)}
                  >
                    Ajouter au panier
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Products;
