import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { addCart } from "../redux/action";
import { collection, getDocs } from "firebase/firestore";
import db from "../firebase";

import Skeleton from "react-loading-skeleton";
import "react-loading-skeleton/dist/skeleton.css";
import { Link } from "react-router-dom";
import toast from "react-hot-toast";

const Products = () => {
  const [data, setData] = useState([]);
  const [filter, setFilter] = useState([]);
  const [loading, setLoading] = useState(false);

  const dispatch = useDispatch();

  const addProduct = (product) => {
    dispatch(addCart(product));
    toast.success("Ajouté au panier");
  };

  useEffect(() => {
    const getProducts = async () => {
      setLoading(true);
      try {
        // Récupère les produits depuis Firestore
        const querySnapshot = await getDocs(collection(db, "products"));
        const products = querySnapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data(),
        }));
        setData(products);
        setFilter(products);
      } catch (error) {
        console.error("Erreur lors de la récupération des produits :", error);
      }
      setLoading(false);
    };
    getProducts();
  }, []);

  const Loading = () => (
    <div className="row">
      {[...Array(6)].map((_, index) => (
        <div key={index} className="col-md-4 mb-4">
          <Skeleton height={300} />
        </div>
      ))}
    </div>
  );

  const ShowProducts = () => (
    <>
      <div className="buttons text-center py-3">
        <button className="btn btn-outline-dark m-2" onClick={() => setFilter(data)}>Tout</button>
        <button className="btn btn-outline-dark m-2" onClick={() => filterCategory("Vêtements")}>Vêtements</button>
        <button className="btn btn-outline-dark m-2" onClick={() => filterCategory("Promotions")}>Promotions</button>
      </div>

      <div className="row">
        {filter.map((product) => (
          <div key={product.id} className="col-md-4 mb-4">
            <div className="card h-100 text-center">
              <img src={product.image} className="card-img-top p-3" alt={product.name} height="300" />
              <div className="card-body">
                <h5 className="card-title">{product.name}</h5>
                <p className="card-text">{product.description.substring(0, 50)}...</p>
              </div>
              <ul className="list-group list-group-flush">
                <li className="list-group-item lead">${product.price}</li>
              </ul>
              <div className="card-body">
                <Link to={`/product/${product.id}`} className="btn btn-dark m-1">Voir le produit</Link>
                <button className="btn btn-dark m-1" onClick={() => addProduct(product)}>Ajouter au panier</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </>
  );

  const filterCategory = (category) => {
    const updatedList = data.filter((item) => item.category === category);
    setFilter(updatedList);
  };

  return (
    <div className="container my-3 py-3">
      <h2 className="display-5 text-center">Nos Produits</h2>
      <hr />
      {loading ? <Loading /> : <ShowProducts />}
    </div>
  );
};

export default Products;
