import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { addCart } from "../redux/action";

import Skeleton from "react-loading-skeleton";
import "react-loading-skeleton/dist/skeleton.css";

import { Link } from "react-router-dom";
import toast from "react-hot-toast";

const Products = () => {
  const [data, setData] = useState([]);
  const [filter, setFilter] = useState([]);
  const [loading, setLoading] = useState(false);

  const dispatch = useDispatch();

  const addProduct = (product) => {
    dispatch(addCart(product));
    toast.success("Added to cart");
  };

  useEffect(() => {
    const getProducts = async () => {
      setLoading(true);
      try {
        const response = await fetch("http://localhost:5000/products");
        const result = await response.json();
        setData(result);
        setFilter(result);
      } catch (error) {
        console.error("Error fetching products:", error);
      }
      setLoading(false);
    };
    getProducts();
  }, []);

  const Loading = () => {
    return (
      <div className="row">
        {[...Array(6)].map((_, index) => (
          <div key={index} className="col-md-4 mb-4">
            <Skeleton height={300} />
          </div>
        ))}
      </div>
    );
  };

  const filterProduct = (cat) => {
    const updatedList = data.filter((item) => item.category === cat);
    setFilter(updatedList);
  };

  const ShowProducts = () => {
    return (
      <>
        <div className="buttons text-center py-3">
          <button className="btn btn-outline-dark m-2" onClick={() => setFilter(data)}>
            All
          </button>
          <button className="btn btn-outline-dark m-2" onClick={() => filterProduct("Promotions")}>
            Promotions
          </button>
          <button className="btn btn-outline-dark m-2" onClick={() => filterProduct("Téléphonie")}>
            Téléphonie
          </button>
          <button className="btn btn-outline-dark m-2" onClick={() => filterProduct("Informatique")}>
            Informatique
          </button>
        </div>

        <div className="row">
          {filter.map((product) => (
            <div key={product.id} className="col-md-4 mb-4">
              <div className="card h-100 text-center">
                <img src={product.image} className="card-img-top p-3" alt={product.name} height="300" />
                <div className="card-body">
                  <h5 className="card-title">{product.name}</h5>
                  <p className="card-text">{product.description.substring(0, 50)}...</p>
                </div>
                <ul className="list-group list-group-flush">
                  <li className="list-group-item lead">${product.price}</li>
                </ul>
                <div className="card-body">
                  <Link to={`/product/${product.id}`} className="btn btn-dark m-1">
                    Buy Now
                  </Link>
                  <button className="btn btn-dark m-1" onClick={() => addProduct(product)}>
                    Add to Cart
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </>
    );
  };

  return (
    <div className="container my-3 py-3">
      <h2 className="display-5 text-center">Latest Products</h2>
      <hr />
      {loading ? <Loading /> : <ShowProducts />}
    </div>
  );
};

export default Products;
