import { initializeApp } from "firebase/app";
import { getFirestore } from "firebase/firestore";

// Configuration de Firebase (remplace les valeurs par celles de ton projet)
const firebaseConfig = {
  apiKey: "TA_CLE_API",
  authDomain: "TON_DOMAINE.firebaseapp.com",
  projectId: "TON_PROJECT_ID",
  storageBucket: "TON_PROJECT_ID.appspot.com",
  messagingSenderId: "TON_ID_MESSAGERIE",
  appId: "TON_ID_APPLICATION",
};

// Initialiser Firebase
const app = initializeApp(firebaseConfig);

// Initialiser Firestore
const db = getFirestore(app);

export default db;
